import React, { Component } from 'react';
import GoogleMap from 'google-map-react';
import './App.css';
import config from './config.js';
import { Button, TextField } from '@material-ui/core';


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      apiKey: config["API_KEY"],
      center: { lat: 47.606209, lng: -122.332069 },
      zipcode: 98105,
    };
  }

  // const google = window.google
  //const config = require('/config.js');


  // let map;

  sendRequest = async () => {
    if (isNaN(this.state.zipcode) || this.state.zipcode.toString().length !== 5) {
      alert("Please input a valid zipcode of 5 digits!");
    }
    const url = "https://maps.googleapis.com/maps/api/geocode/json?" + this.state.apiKey + "components=postal_code:" + this.state.zipcode 
    try {
      let response = await fetch(url);
      if (!response.ok) {
        alert("The status is wrong! Expected: 200, Was: " + response.status);
        return; // Don't keep trying to execute if the response is bad.
      }
      let text = await response.json();
      this.setState({
        center: text.results[0].geometry.location,
      });
    } catch (e) {
      alert("There was an error contacting the server. Your zip code may be invalid.");
      console.log(e);
    }
  }

  // function initMap() {
  //   map = new google.maps.Map(document.getElementById("map"), {
  //     center: { lat: 47.606209, lng: -122.332069 },
  //     zoom: 13.25,
  //   });
  // }
  handleInputChange = (event) => {
    console.log(event.target.value);
    this.setState({
      zipcode: event.target.value,
    });
    console.log(this.state.zipcode);
  }

  render() {
    return (
      <div className="App">
        <TextField
          type="number"
          id="zipcode"
          label="zipcode"
          value={this.state.zip}
          margin="normal"
          onChange={this.handleInputChange}
        />
        <Button onClick={this.sendRequest}>
          GO
        </Button>
        <GoogleMap
          bootstrapURLKeys={{
            key: this.state.API_KEY,
            center: this.state.center,
            zoom: 13,
            libraries: ['places', 'geometry']

          }}
        />

      </div>
    );
  }
}
export default App;
