import React, { Component } from 'react';
import GoogleMapReact from 'google-map-react';
import config from './config.js';
import { Button, Checkbox, TextField } from '@material-ui/core';
import 'bootstrap/dist/css/bootstrap.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      apiKey: config["API_KEY"],
      center: { lat: 47.606209, lng: -122.332069 },
      zipcode: 98105,
      zoom: 13,
      clothing: false, 
      food: false, 
      essential: false, 
      blood: false
    };
  }

  sendRequest = async () => {
    if (isNaN(this.state.zipcode) || this.state.zipcode.toString().length !== 5) {
      alert("Please input a valid zipcode of 5 digits!");
      return;
    }
    const url = "https://maps.googleapis.com/maps/api/geocode/json?components=postal_code:" + this.state.zipcode + "&key=" + this.state.apiKey;
    try {
      const response = await fetch(url);
      if (!response.ok) {
        alert("The status is wrong! Expected: 200, Was: " + response.status);
        return; // Don't keep trying to execute if the response is bad.
      }
      const text = await response.json();
      this.setState({
        center: text.results[0].geometry.location,
        zoom: 13,
      });
    } catch (e) {
      alert("There was an error contacting the server. Your zip code may be invalid.");
      console.log(e);
    }
  }

  featuresRequest(query) {
    var request = {
      location: position,
      radius: '500',
      query: query,
      fields: ['name', 'icon', 'photos', 'formatted_address', 'geometry', 'opening_hours']
    };
    return request
  }

  bloodRequest() {
    var request = featuresRequest('blood bank');

    if (!bloodClicked) {
      bloodClicked = true;
      service = new google.maps.places.PlacesService(map);
      service.textSearch(request, callback);

    }
    else {
      bloodClicked = false;
      clearMarkers(null);
    }
  }

  clothesRequest() {
    var request = featuresRequest('clothing donation');

    if (!clothesClicked) {
      clothesClicked = true;
      service = new google.maps.places.PlacesService(map);
      service.textSearch(request, callback);
    }
    else {
      clothesClicked = false;
      clearMarkers(null);
    }
  }

  foodRequest() {
    var request = featuresRequest('food bank');

    if (!foodClicked) {
      foodClicked = true;
      service = new google.maps.places.PlacesService(map);
      service.textSearch(request, callback);
    }
    else {
      foodClicked = false;
      clearMarkers(null);
    }
  }

  essentialRequest() {
    var request = featuresRequest('items donation');

    if (!essentialClicked) {
      essentialClicked = true;
      service = new google.maps.places.PlacesService(map);
      service.textSearch(request, callback);
    }
    else {
      essentialClicked = false;
      clearMarkers(null);
    }
  }

  callback(results, status) {
    if (status === google.maps.places.PlacesServiceStatus.OK) {
      for (var i = 0; i < results.length; i++) {
        createMarker(results[i], map);
      }
    }
  }

  handleInputChange = (event) => {
    this.setState({
      zipcode: event.target.value,
    });
  }

  clothesChange = (event) => {
    this.setState({
      clothes: event.target.value
    });
  }

  render() {
    return (
      <div className="App">
        <nav class="navbar navbar-expand-lg navbar-light bg-light">
          <div class="container-fluid">
            <a class="navbar-brand" href="#">Donation Buddy</a>
            <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
              <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarNav">
              <ul class="navbar-nav">
                <li class="nav-item">
                  <a class="nav-link active" aria-current="page" href="index.js">Home</a>
                </li>
                <li class="nav-item">
                  <a class="nav-link" href="services.js">Services</a>
                </li>
                <li class="nav-item">
                  <a class="nav-link" href="#">Contact</a>
                </li>
                <li class="nav-item">
                  <a class="nav-link" href="#">About Us </a>
                </li>
              </ul>
            </div>
          </div>
        </nav>
        <div class="row">
          <div class="col-md-5  m-5 p-5">
            <TextField
              type="number"
              id="zipcode"
              label="enter your zipcode"
              placeholder="98105"
              value={this.state.zip}
              onChange={this.handleInputChange}
            />
            <div id="features">
              <br></br>
              <h5>Features</h5>
              <Checkbox
                id="clothes"
                label="Clothes"
                onChange={this.clothesChange}
              /> Clothing
              <br></br>
              <Checkbox
                id="foodBanks"
                label="Food Banks"
                onChange={this.foodChange}
              /> Food Banks
              <br></br>
              <Checkbox
                id="essentialProducts"
                label="Essential Products"
                onChange={this.essentialChange}
              /> Essential Products
              <br></br>
              <Checkbox
                id="blood"
                label="Blood"
                onChange={this.bloodChange}
              /> Blood
            </div>
            <Button onClick={this.sendRequest}>
              GO
            </Button>
          </div>
          <div class="col-md-5 m-3">
            <div style={{ height: '80vh', width: '100%' }}>
              <GoogleMapReact
                bootstrapURLKeys={{ key: this.state.apiKey }}
                center={this.state.center}
                zoom={this.state.zoom}
              />
            </div>
          </div>
        </div>


      </div >
    );
  }
}
export default App;
